        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div>
            <h3>{state.name}</h3>
          </div>
          <Divider variant="fullWidth" className={classes.divider} />
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="number"
              value={state.wages}
              onChange={handleChange}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Allowances"
              name="allowances"
              variant="filled"
              type="number"
              value={state.allowances}
              onChange={handleChange}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Deductions"
              name="deductions"
              variant="filled"
              type="number"
              value={state.deductions}
              onChange={handleChange}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nettpay"
              variant="filled"
              type="number"
              value={state.nettpay}
              onChange={handleChange}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

