import { atom, selector } from "recoil";

export const departmentsState = atom({
  key: "departmentsState",
  default: {
    name: "",
  },
});

export const departmentsSelector = selector({
  key: "departmentsSelector",
  get: async ({ get }) => {
    const res = await loadDepartments();
    return res;
  },
});
