import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import { TextField, Grid,Paper, Container, Box } from "@material-ui/core";
import CardLayout from "../helpers/CardLayout";
import CardLayout2 from "../helpers/CardLayout2";
import CardLayout3 from "../helpers/CardLayout3";
import Copyright from "../components/Copyright";
import { CustomDialog } from "../helpers/CustomDialog";

import { useEmployeesContext } from "../context/employees_context";
import { useTablesContext } from "../context/tables_context";
import { useTrainingsContext } from "../context/trainings_context";
import LeaveTableView from "./LeaveTableView";

const drawerWidth = 240;

const EmployeeView = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [familydata, setFamilydata] = useState([]);
  const [educationdata, setEducationdata] = useState([]);
  const [experiencedata, setExperiencedata] = useState([]);
  const [trainingdata, setTrainingdata] = useState([]);
  const [isPersonalDialogOpen, setIsPersonalDialogOpen] = useState(false);
  const [isFamilyDialogOpen, setIsFamilyDialogOpen] = useState(false);
  const [isEducationDialogOpen, setIsEducationDialogOpen] = useState(false);
  const [isExperienceDialogOpen, setIsExperienceDialogOpen] = useState(false);
  const [isTrainingDialogOpen, setIsTrainingDialogOpen] = useState(false);

  const {
    editEmployeeID,
    getSingleEmployee,
    single_employee,
    single_employee_loading,
  } = useEmployeesContext();
  const { singlebatch_training } = useTrainingsContext();

  const {
    loadDepartments,
    loadDesignations,
    loadSingleBatchFamily,
    loadSingleBatchEducation,
    singlebatchfamily,
    singlebatcheducation,
    singlebatchexperience,
  } = useTablesContext();

  useEffect(() => {
    getSingleEmployee(editEmployeeID);
    //loadDepartments();
    //loadDesignations();
  }, []);

  //useEffect(() => {}, [singlebatchfamily]);

  
  const handleFamilyDialogOpen = () => {
    setFamilydata([]);
    setFamilydata([...singlebatchfamily]);
    setIsFamilyDialogOpen(true);
  };

  const handleFamilyDialogClose = () => {
    setIsFamilyDialogOpen(false);
    loadSingleBatchFamily(editEmployeeID);
  };


  return (
    <div>
      <div className={classes.appBarSpacer} />
      <div style={{ paddingLeft: 50 }}>
        <h2>Welcome Admin!</h2>
        <h3>Dashboard</h3>
      </div>
      <Container maxWidth="lg" className={classes.container}>
        <Grid container spacing={3}>
          {/* Chart */}
          <Grid item xs={12} md={8} lg={12}>
            {/* <Paper className={fixedHeightPaper}>
              <div>Chart</div>
            </Paper> */}
            <CardLayout3
              title="Chart"
              // handleClick={handleEarningsDialogOpen}
            >
              <h2>Chart</h2>
              {/* <Payslip_ViewEarnings /> */}
            </CardLayout3>
          </Grid>
          {/* Recent Deposits */}
          <Grid item xs={6} md={8} lg={6}>
            <CardLayout3
              title="Leaves pending for approval"
              // handleClick={handleEarningsDialogOpen}
            >
              <LeaveTableView />
            </CardLayout3>
          </Grid>
          {/* Recent Orders */}
          <Grid item xs={6} md={8} lg={6}>
            <CardLayout3
              title="Leaves"
              // handleClick={handleEarningsDialogOpen}
            >
              <h2>Expenses pending for approval</h2>
              {/* <Payslip_ViewEarnings /> */}
            </CardLayout3>
          </Grid>
        </Grid>
        <Box pt={4}>
          <Copyright />
        </Box>
      </Container>
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0 8px",
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: "none",
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: "relative",
    whiteSpace: "nowrap",
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: "hidden",
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up("sm")]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: "100vh",
    overflow: "auto",
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
  },
  fixedHeight: {
    height: 240,
  },
}));

export default EmployeeView;
