import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import MaterialTable from "material-table";
import { Controller, useForm } from "react-hook-form";
import clsx from "clsx";
import axios from "axios";
import { useAsync } from "react-async";
import { useHistory } from "react-router-dom";
import {
  Button,
  Checkbox,
  Paper,
  Grid,
  Divider,
  TextField,
  MenuItem,
  ListSubheader,
} from "@material-ui/core";
import { employees_url } from "../utils/constants";
import { useEmployeesContext } from "../context/employees_context";
import {
  payslips,
  usePayslipsContext,
  updatePayslipsData,
} from "../context/payslips_context";
import { useTablesContext } from "../context/tables_context";
import { payrunState, paydataState } from "./data/atomdata";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";

const drawerWidth = 240;
const url = "https://course-api.com/react-tabs-project";

const Payrunbatch = () => {
  let history = useHistory();
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const { register, handleSubmit, control } = useForm();
  const { loadEmployees, employees, employees_loading } = useEmployeesContext();
  const {
    getPayslipitems,
    payslipitems,
    payslipitems_loading,
    payslipsdata,
    setPayslipsData,
    resetPayslipsData,
    updatePayslipsData,
    getSingleBatchPayslip,
    singlebatchpayslip,
    singlebatch_payslip_loading,
    singlebatch_payslip_error,
  } = usePayslipsContext();
  const { loadPayitems, payitems, payitems_loading, payitems_loading_error } =
    useTablesContext();
  //const [payitemsdata] = useRecoilState(paydataState);
  const payrundata = useRecoilValue(payrunState);
  const [loadFormdata, setLoadFormdata] = useState(false);
  const [formdata, setFormdata] = useState([]);
  const [rowindex, setRowIndex] = useState(0);

  const handleClick = () => {};

  const onFormSubmit = (data) => {
    console.log("Data", data);
  };

  useEffect(() => {
    console.log("useeffect");
    loadPayitems();
    getSingleBatchPayslip(payrundata.payrun);
  }, []);

  const handleOnChange = (e) => {
    e.preventDefault();
    setFormdata({
      ...formdata,
      [e.target.name]: e.target.value,
    });
  };
  const handleEmpButtonClick = (index) => {
    const paydata = singlebatchpayslip[index];
    setFormdata({ ...paydata });
    setFormdata({ ...paydata });
    console.log(formdata);
  };

  const ShowPaydata = () => {
    return (
      <div>
        <form onSubmit={onFormSubmit}>
          <Controller
            name="payperiod"
            control={control}
            defaultValue={payrundata.period}
            render={({ field: { onChange, value } }) => (
              <TextField
                label="Pay Period"
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={value}
                onChange={onChange}
                className={classes.textField}
                InputProps={{
                  readOnly: true,
                }}
              />
            )}
          />
          <Controller
            name="payperiod"
            control={control}
            defaultValue={payrundata.period}
            render={({ field: { onChange, value } }) => (
              <TextField
                label="Pay Period"
                variant="filled"
                type="currency"
                style={{ width: 100 }}
                value={value}
                onChange={onChange}
                className={classes.textField}
                InputProps={{
                  readOnly: true,
                }}
              />
            )}
          />
          <Button type="submit">Submit</Button>
        </form>
      </div>
    );
  };

  if (singlebatch_payslip_loading) {
    return (
      <div>
        <h2>Loading...</h2>
      </div>
    );
  }

  if (singlebatch_payslip_error) {
    history.push("/error");
  }

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
        <Grid container direction="row" style={{ border: "1px solid white" }}>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Employees</h2>
          </Grid>
          <Grid
            item
            sm={10}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Payslip</h2>
          </Grid>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              {singlebatchpayslip &&
                singlebatchpayslip.map((item, index) => {
                  return (
                    <Button
                      onClick={(e) => {
                        setRowIndex(index);
                        handleEmpButtonClick(index);
                      }}
                    >
                      <div key={item.id}> {item.name}</div>
                    </Button>
                  );
                })}
            </div>
          </Grid>
          <Grid
            item
            sm={10}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <ShowPaydata />
          </Grid>
        </Grid>
      </section>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "80vw",
    margin: "1rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },
  divider: {
    // Theme Color, or use css color in quote
    background: "white",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 350,
  },
}));

export default Payrunbatch;
