import React from "react";
import { Grid, TextField, Divider } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const PayForm = ({ formdata, setFormdata }) => {
  const classes = useStyles();
  console.log(formdata);
  return (
    <form>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Allowances</h2>
        </Grid>

        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Deductions</h2>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div className={classes.divContainer}>
            <TextField
              label="Allowance"
              name="allows_type1"
              variant="filled"
              type="text"
              style={{ width: 100 }}
              value={formdata.allows_type1}
              onChange={setFormdata}
            />
            <Divider
              orientation="vertical"
              variant="fullWidth"
              className={classes.divider}
            />
            <TextField
              label="Amount"
              name="allows_type1amt"
              variant="filled"
              type="currency"
              style={{ width: 50 }}
              value={formdata.allows_type1amt}
              onChange={setFormdata}
            />
          </div>
          <div>
            <TextField
              label=""
              name="allows_type2"
              variant="filled"
              type="text"
              value={formdata.allows_type2}
              onChange={setFormdata}
            />
          </div>
          <div>
            <TextField
              label=""
              name="allows_type3"
              variant="filled"
              type="text"
              value={formdata.allows_type3}
              onChange={setFormdata}
            />
          </div>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <TextField
            label="Bank AC No"
            name="bank_acno"
            variant="filled"
            type="currency"
            style={{ width: 100 }}
            value={formdata.bank_acno}
            onChange={(e) => setFormdata(e)}
          />
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Name"
              name="name"
              variant="filled"
              type="currency"
              value={formdata.name}
              onChange={setFormdata}
            />
          </div>
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="currency"
              value={formdata.wages}
              onChange={setFormdata}
            />
          </div>
          <div>
            <TextField
              label="Total Allowances"
              name="allowances"
              variant="filled"
              type="currency"
              value={formdata.allowances}
              onChange={setFormdata}
            />
          </div>
          <div>
            <TextField
              label="Total Deductions"
              name="deductions"
              variant="filled"
              type="currency"
              value={formdata.deductions}
              onChange={setFormdata}
            />
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nettpay"
              variant="filled"
              type="currency"
              value={formdata.nettpay}
              onChange={setFormdata}
            />
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "80vw",
    margin: "1rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },
  divider: {
    // Theme Color, or use css color in quote
    background: "white",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 350,
  },
  divContainer: {
    display: "flex",
    flexDirection: "row",
    alignsItem
}
}));

export default PayForm;
