import React, { useState, useEffect } from "react";

import MaterialTable from "material-table";
import { makeStyles } from "@material-ui/core/styles";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import CheckIcon from "@material-ui/icons/Check";
import SearchIcon from "@material-ui/icons/Search";
import { useHistory, Link } from "react-router-dom";

import { useTablesContext } from "../context/tables_context";

const columns = [
  {
    title: "Name",
    field: "name",
  },
];

export default function UpdateAllowances() {
  let history = useHistory();
  const classes = useStyles();
  const {
    loadAllowances,
    allowances,
    allowances_loading,
    addAllowance,
    deleteAllowance,
    updateallowance,
  } = useTablesContext();

  useEffect(() => {
    loadAllowances();
  }, []);

  const update_Allowance = (data) => {
    updateLeave({ id, ...data });
  };

  const add_Allowance = async (data) => {
    addAllowance(data);
    loadAllowances();
  };

  const delete_Allowance = (data) => {
    const { id } = data;
    deleteAllowance(id);
    loadAllowances();
  };

  if (allowances_loading) {
    return <div>Loading...</div>;
  }
  return (
    <div className={classes.root}>
      {/* <h1>Expenses Claims Application</h1> */}

      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={allowances}
          title="Allowances"
          editable={{
            onRowAdd: (newData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  add_Allowance(newData);

                  resolve();
                }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  update_Allowance(newData);
                  //   const dataUpdate = [...data];
                  //   const index = oldData.tableData.id;
                  //   dataUpdate[index] = newData;
                  //   setData([...dataUpdate]);

                  resolve();
                }, 1000);
              }),
            onRowDelete: (oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  console.log(oldData);
                  delete_Allowance(oldData);
                  //   const dataDelete = [...data];
                  //   const index = oldData.tableData.id;
                  //   dataDelete.splice(index, 1);
                  //   setData([...dataDelete]);

                  resolve();
                }, 1000);
              }),
          }}
        />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
