import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import {
  Button,
  Paper,
  Grid,
  Icon,
  Divider,
  TextField,
  NativeSelect,
  InputLabel,
} from "@material-ui/core";
import { useRecoilState } from "recoil";

//const drawerWidth = 240;

const DailyAllowancesAddPeriod = ({ handleDialogClose }) => {
  let date = new Date();
  let longMonth = date.toLocaleString("en-us", { month: "long" });
  console.log("date", date, longMonth);
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  const [alert, setAlert] = useState(false);
  const [error, setError] = useState(false);

  function formatDate(string) {
    var options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(string).toLocaleDateString("en-GB", options);
  }

  function formatPayrun() {
    // const yy =
    //   input.fromdate.substring(0, 4) + "-" + input.fromdate.substring(5, 7);
    // const mm = input.fromdate.substring(5, 7);
    // const d = input.fromdate;
  }

  function daysInMonth(month, year) {
    return new Date(year, month, 0).getDate();
  }

  const handleChange = (e) => {
    //setInput({ ...input, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    handleDialogClose();
  };

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
        <Grid
          direction="row"
          container
          spacing={1}
          // style={{ border: "1px solid white" }}
        >
          <Grid
            container
            item
            sm={3}
            style={{ border: "1px solid white" }}
            direction="column"
            align="left"
          >
            <article className={classes.jobinfo}>
              <h2>Build Site Allowances</h2>
              <form onSubmit={handleSubmit}>
                <div>
                  <TextField
                    label="Period"
                    variant="filled"
                    required
                    defaultValue=""
                    style={{ width: "100%" }}
                    name="period"
                    //value={input.payfreq}
                    onChange={(e) => handleChange(e)}
                  ></TextField>
                </div>
                <div>
                  <TextField
                    label="Location"
                    variant="filled"
                    required
                    defaultValue=""
                    style={{ width: "100%" }}
                    name="location"
                    //value={input.payfreq}
                    onChange={(e) => handleChange(e)}
                  ></TextField>
                </div>
                <div>
                  <TextField
                    label="Manager"
                    variant="filled"
                    required
                    defaultValue=""
                    style={{ width: "100%" }}
                    name="manager"
                    //value={input.payfreq}
                    onChange={(e) => handleChange(e)}
                  ></TextField>
                </div>
                <div>
                  <TextField
                    label="Jo"
                    variant="filled"
                    required
                    defaultValue=""
                    style={{ width: "100%" }}
                    name="siteallows_fee"
                    //value={input.payfreq}
                    onChange={(e) => handleChange(e)}
                  ></TextField>
                </div>

                <div>
                  <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    className={classes.button}
                  >
                    Submit <Icon className={classes.rightIcon}>send</Icon>
                  </Button>
                </div>
              </form>
            </article>
          </Grid>
          <Divider
            orientation="vertical"
            flexItem
            style={{ background: "white" }}
          />
          <Grid
            container
            item
            sm={3}
            style={{ border: "1px solid white" }}
            align="right"
          ></Grid>
        </Grid>
      </section>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  button: {
    margin: theme.spacing(1),
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "95vw",
    margin: "5rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },
  rightIcon: {
    marginLeft: theme.spacing(1),
  },
  formLabel: {
    fontSize: 12,
    textAlign: "left",
    marginLeft: 8,
    marginTop: 5,
  },
}));

export default DailyAllowancesAddPeriod;
