                        }}
                      />
                    )}
                  />
                </div>
                <div>
                  <Controller
                    name="deductions"
                    control={control}
                    defaultValue={payslipsdata.pay[rowindex][0].deductions}
                    render={({ field: { onChange, value } }) => (
                      <TextField
                        label="Total Deductions"
                        variant="filled"
                        type="currency"
                        style={{ width: 100 }}
                        value={value}
                        onChange={onChange}
                        className={classes.textField}
                        InputProps={{
                          readOnly: true,
                        }}
                      />
                    )}
                  />
                </div>
                <div>
                  <Divider variant="fullWidth" className={classes.divider} />
                  <Controller
                    name="nettpay"
                    control={control}
                    defaultValue={payslipsdata.pay[rowindex][0].nettpay}
                    render={({ field: { onChange, value } }) => (
                      <TextField
                        label="Nett Pay"
                        variant="filled"
                        type="currency"
                        style={{ width: 100 }}
                        value={value}
                        onChange={onChange}
                        className={classes.textField}
                        InputProps={{
                          readOnly: true,
                        }}
                      />
                    )}
                  />
                </div>
              </div>
            </Grid>
          </Grid>
        </form>
      </section>
    </Paper>
  );
};

