import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Paper from "@material-ui/core/Paper";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@material-ui/core";
import CardLayout from "../helpers/CardLayout";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@material-ui/core";

import { useEmployeesContext } from "../context/employees_context";
import { useTablesContext } from "../context/tables_context";
import Emp_ViewFamily from "./Emp_ViewFamily"
import Emp_ViewEducations from "./Emp_ViewEducations";
import Emp_ViewExperiences from "./Emp_ViewExperiences";
import Emp_ViewExperie from "./Emp_ViewLeave";

const drawerWidth = 240;

// const CardLayout = ({ title, children }) => {
//   const classes = useStyles();
//   return (
//     <div>
//       <CardLayout title="Personal Information">
//         <h2>details</h2>
//       </CardLayout>
//     </div>
//   );
// };

const EmployeeView = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [profile, setProfile] = useState({});
  const [family, setFamily] = useState({});

  const {
    employees,
    addEmployee,
    editEmployeeID,
    employees_loading,
    updateEmployee,
    deleteEmployee,
    loadEmployees,
    getSingleEmployee,
    setEditEmployeeID,
    setIsEditingOn,
    setIsEditingOff,
    resetSingleEmployee,
    single_employee,
    single_employee_loading,
  } = useEmployeesContext();

  const {
    loadSingleBatchFamily,
    loadSingleBatchEducation,
    singlebatchfamily,
    singlebatcheducation,
    addFamily,
    deleteFamily,
    updateFamily,
    singlebatch_family_loading,
    singlebatch_family_error,
    singlebatch_education_loading,
  } = useTablesContext();

  useEffect(() => {
    getSingleEmployee(editEmployeeID);
  }, []);

  
  const load_Profile = () => {
    //const { empid, name, birthdate, gender, email, phone, ic_no, ic_expirydate } = single_employee;
    setProfile({ ...single_employee });
  };

  const load_Family = () => {
    //loadSingleBatchFamily(editEmployeeID);
    setFamily({ ...singlebatchfamily });
    console.log(family);
    console.log(singlebatchfamily);
  };

  if (single_employee_loading) {
    return (
      <div>
        <h2>Loading...Employee</h2>
      </div>
    );
  }
  

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <div className={classes.root}>
        <Grid container spacing={3}>
          <Grid xs={12} md={8} lg={12}>
            <CardLayout title="Employee Profile">
              <div>
                <form className={classes.form}>
                  <TextField
                    label="Name"
                    name="name"
                    value={single_employee.name}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    label="Phone"
                    name="phone"
                    value={single_employee.phone}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    label="Email"
                    name="email"
                    value={single_employee.email}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    label="Birth Date"
                    name="birthdate"
                    value={single_employee.birhdate}
                    type="date"
                    InputLabelProps={{
                      shrink: true,
                    }}
                  />
                  <TextField
                    label="Gender"
                    name="gender"
                    value={single_employee.gender}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </form>
              </div>
            </CardLayout>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Personal Information">
                <div>
                  <form className={classes.form}>
                  
                      <TextField
                        label="I/C No"
                        name="icno"
                       
                        value={single_employee.ic_no}
                        InputProps={{
                          readOnly: true,
                        }}
                      />

                      <TextField
                        label="I/C Expiry Date"
                        name="ic_expirydate"
                        value={single_employee.ic_expirydate}
                        type="date"
                        InputProps={{
                          readOnly: true,
                        }}
                        InputLabelProps={{
                          shrink: true,
                        }}
                      />
                 
                    <TextField
                      label="Passport No"
                      name="passportno"
                      value={single_employee.passportno}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                    <TextField
                      label="Passport Expiry Date"
                      name="passport_expirydate"
                      value={single_employee.passport_expirydate}
                      type="date"
                      InputLabelProps={{
                        shrink: true,
                      }}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                  
                      <TextField
                        label="Work Permit No"
                        name="workpermitno"
                        value={single_employee.workpermitno}
                        InputProps={{
                          readOnly: true,
                        }}
                      />
                      <TextField
                        label="W/P Expiry Date"
                        name="workpermit_expirydate"
                        value={single_employee.workpermit_expirydate}
                        type="date"
                        InputLabelProps={{
                          shrink: true,
                        }}
                        InputProps={{
                          readOnly: true,
                        }}
                      />
                  
                    <div>
                      <TextField
                        label="Nationality"
                        name="nationality"
                        value={single_employee.nationality}
                        InputProps={{
                          readOnly: true,
                        }}
                      />
                    </div>
                    <div>
                      <TextField
                        label="Marital Status"
                        name="marital_status"
                        value={single_employee.marital_status}
                        InputProps={{
                          readOnly: true,
                        }}
                      />
                    </div>
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Family">
                <Emp_ViewFamily />
              </CardLayout>
            </div>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Education">
                <div>
                  <Emp_ViewEducations />
                </div>
              </CardLayout>
            </div>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Experiences">
                <div>
                  <Emp_ViewExperiences />
                </div>
              </CardLayout>
            </div>
          </Grid>

          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Training">
                <div>
                  <form>
                    <TextField label="Name" name="name" value="name" />
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Leaves">
                <div>
                  <form>
                    <TextField label="Name" name="name" value="name" />
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>
        </Grid>
      </div>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
