import React, { useState, useReducer, useEffect } from "react";
import { Button, Icon, TextField, Paper, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import { useEmployeesContext } from "../context/employees_context";
import { useForm } from "react-hook-form";

const initial_values = {
  name: "",
  gender: "",
  ic_no: "",
  email: "",
  age: "",
};

const EmployeeForm = () => {
  const classes = useStyles();
  const { isEditing, single_employee } = useEmployeesContext();
  const { name, ic_no, gender, age, email } = single_employee || {};
  const { register, handleSubmit, watch, errors } = useForm();

  const onSubmit = (data) => console.log(data);

  return (
    <div>
      <Paper className={classes.root}>
        <Typography variant="h5" component="h3">
          EMPLOYEE FORM
        </Typography>
        <Typography component="p">Employee Particulars</Typography>

        <form onSubmit={onSubmit}>
          <div>
            <TextField
              label="Name"
              id="margin-normal"
              name="name"
              defaultValue={name}
              className={classes.textField}
              helperText="Enter your full name"
              ref={register}
            />
          </div>
          <div>
            <TextField
              label="IC No"
              id="margin-normal"
              name="ic_no"
              defaultValue={ic_no}
              className={classes.textField}
              helperText="Enter your I/C No"
              ref={register}
            />
          </div>
          <div>
            <TextField
              label="Gender"
              id="margin-normal"
              name="gender"
              defaultValue={gender}
              className={classes.textField}
              helperText="Enter your gender"
              ref={register}
              select
            >
              <MenuItem value="Maine">Male</MenuItem>
              <MenuItem value="Female">Female</MenuItem>
            </TextField>
          </div>
          <div>
            <TextField
              label="Age"
              id="margin-normal"
              name="age"
              defaultValue={age}
              className={classes.textField}
              helperText="Enter your age"
              ref={register}
            />
          </div>
          <div>
            <TextField
              label="Email"
              id="margin-normal"
              name="email"
              defaultValue={email}
              className={classes.textField}
              helperText="e.g. name@gmail.com"
              ref={register}
            />
          </div>
          <div>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              className={classes.button}
            >
              Submit <Icon className={classes.rightIcon}>send</Icon>
            </Button>
          </div>
        </form>
      </Paper>
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
  leftIcon: {
    marginRight: theme.spacing(1),
  },
  rightIcon: {
    marginLeft: theme.spacing(1),
  },
  iconSmall: {
    fontSize: 20,
  },
  root: {
    padding: theme.spacing(3, 2),
  },
  container: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 400,
  },
}));

export default EmployeeForm;
