import React from "react";
import { Grid, TextField } from "@material-ui/core";

const PayForm = ({ formdata, setFormdata }) => {
  console.log(formdata);
  return (
    <form>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Allowances</h2>
        </Grid>

        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Deductions</h2>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Name"
              name="name"
              variant="filled"
              type="currency"
              style={{ width: 100 }}
              value={formdata.name}
              onChange={setFormdata}
            />
          </div>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <TextField
            label="Bank AC No"
            name="bank_acno"
            variant="filled"
            type="currency"
            style={{ width: 100 }}
            value={formdata.bank_acno}
            onChange={(e) => setFormdata(e)}
          />
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="currency"
              style={{ width: 100 }}
              value={formdata.wages}
              onChange={setFormdata}
            />
          </div>
          <div>
            <TextField
              label="Allowances"
              name="wages"
              variant="filled"
              type="currency"
              style={{ width: 100 }}
              value={formdata.wages}
              onChange={setFormdata}
            />
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

export default PayForm;
