import React from "react";
import { Grid, TextField, Divider } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const PayForm = ({ formdata, setFormdata }) => {
  const classes = useStyles();
  console.log(formdata);
  return (
    <form>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Allowances</h2>
        </Grid>

        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Deductions</h2>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type1"
                  variant="filled"
                  type="text"
                  value={formdata.allows_type1}
                  onChange={setFormdata}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type2"
                  variant="filled"
                  type="text"
                  value={formdata.allows_type2}
                  onChange={setFormdata}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type3"
                  variant="filled"
                  type="text"
                  value={formdata.allows_type3}
                  onChange={setFormdata}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type4"
                  variant="filled"
                  type="text"
                  value={formdata.allows_type4}
                  onChange={setFormdata}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type5"
                  variant="filled"
                  type="text"
                  value={formdata.allows_type5}
                  onChange={setFormdata}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type6"
                  variant="filled"
                  type="text"
                  value={formdata.allows_type6}
                  onChange={setFormdata}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
