import React, { useState, useEffect } from "react";
import {
  Button,
  ButtonGroup,
  Grid,
  Icon,
  TextField,
  Divider,
  ListSubheader,
  MenuItem,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { singlebatchpayslip } from "../context/payslips_context";
import { usePayslipsContext } from "../context/payslips_context";

const initial_state = [
  {
    name: "",
    period: "",
    date: "",
    payrun: "",
    nett_pay: 0,
    bank_name: "",
    bank_acno: "",
    tap_acno: "",
    tap_amount: 0,
    scp_acno: "",
    scp_amount: 0,
    empid: "",
    status: "",
    allows_type1: " ",
    allows_type1amt: 0,
    allows_type2: " ",
    allows_type2amt: 0,
    allows_type3: " ",
    allows_type3amt: 0,
    allows_type4: " ",
    allows_type4amt: 0,
    allows_type5: " ",
    allows_type5amt: 0,
    allows_type6: " ",
    allows_type6amt: 0,
    allows_type7: " ",
    allows_type7amt: 0,
    allows_type8: " ",
    allows_type8amt: 0,
    deducts_type1: " ",
    deducts_type1amt: 0,
    deducts_type2: " ",
    deducts_type2amt: 0,
    deducts_type3: " ",
    deducts_type3amt: 0,
    deducts_type4: "",
    deducts_type4amt: 0,
    deducts_type5: "",
    deducts_type5amt: 0,
    deducts_type6: "",
    deducts_type6amt: 0,
    deducts_type7: "",
    deducts_type7amt: 0,
    deducts_type8: "",
    deducts_type8amt: 0,
  },
];

const PaySummary = ({
  formdata,
  setFormdata,
  loadFormdata,
  setLoadFormdata,
  payitems,
  setLoadUpdatedata,
  rowindex,
}) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);
  const { singlebatchpayslip } = usePayslipsContext();

 

  const handleChange = (e) => {
    e.preventDefault();
    const { name, type, value } = e.target;
    const val = type === "number" ? parseFloat(value) : value;
    //setFormInput({ [name]: val });
    setState({ ...state, [name]: val });
    Update_Empdata({ name: name, value: val });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log("submitform");
    handleCalc();
  };

  const handleCalc = (e) => {
    const wages = state.basic_salary;
    const totalTAP = Math.ceil(state.wages * 0.05);
    const totalSCP =
      Math.round((state.wages + Number.EPSILON) * 0.035 * 100) / 100;

    const allows =
      parseInt(state.allows_type2amt, 10) +
      parseInt(state.allows_type3amt, 10) +
      parseInt(state.allows_type4amt, 10) +
      parseInt(state.allows_type5amt, 10) +
      parseInt(state.allows_type6amt, 10) +
      parseInt(state.allows_type7amt, 10) +
      parseInt(state.allows_type8amt, 10);

    const deducts =
      parseInt(state.deducts_type1amt, 10) +
      parseInt(state.deducts_type2amt, 10) +
      parseInt(state.deducts_type3amt, 10) +
      parseInt(state.deducts_type4amt, 10) +
      parseInt(state.deducts_type5amt, 10) +
      parseInt(state.deducts_type6amt, 10) +
      parseInt(state.deducts_type7amt, 10) +
      parseInt(state.deducts_type8amt, 10);

    const nettPay = state.wages - totalTAP - totalSCP + allows - deducts;
    setState({
      ...state,
      allowances: allows,
      deductions: deducts,
      tapamount: totalTAP,
      scpamount: totalSCP,
      nett_pay: nettPay,
    });
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Allowances</h2>
        </Grid>

        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Deductions</h2>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            ></Grid>
            <Grid
              item
              sm={4}
              align="center"
              style={{ border: "1px solid white" }}
            ></Grid>
          </Grid>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            ></Grid>
            <Grid
              item
              sm={4}
              align="center"
              style={{ border: "1px solid white" }}
            ></Grid>
          </Grid>
        </Grid>
        <Grid
          item
          sm={4}
          align="center"
          style={{ border: "1px solid white" }}
        ></Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  button: {
    margin: theme.spacing(1),
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "80vw",
    margin: "1rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },
  divider: {
    // Theme Color, or use css color in quote
    background: "white",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 350,
  },
  divContainer: {
    display: "flex",
    flexDirection: "row",
  },
}));

export default PaySummary;
