            />
          </div>
        </Grid>
        <Grid item sm={2} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Total SCP"
              name="totalscp"
              variant="filled"
              type="currency"
              value={payrundata.totalscp}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
        <Grid item sm={2} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Total Allowances"
              name="totalallows"
              variant="filled"
              type="currency"
              value={payrundata.totalallows}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
