            />
          </div>
        </Grid>
        <Grid item sm={2} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Total Deductions"
              name="totaldeducts"
              variant="filled"
              type="currency"
              value={payrundata.totaldeducts}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
        <Grid item sm={2} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Total Payroll"
              name="totalpayroll"
              variant="filled"
              type="currency"
              value={payrundata.totalpayroll}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
      </Grid>
    </form>
  );
};

