import React, { useState, useEffect, useMemo } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import { useHistory } from "react-router-dom";
import {
  Button,
  ButtonGroup,
  Paper,
  Grid,
  Icon,
  Divider,
} from "@material-ui/core";
import PayForm from "./PayForm";
import PaySummary from "./PaySummary";
import { usePayslipsContext } from "../context/payslips_context";
import { useTablesContext } from "../context/tables_context";
import { payrunState, paydataState } from "./data/atomdata";
import { useRecoilValue } from "recoil";

const drawerWidth = 240;

const Payrunbatch = () => {
  let history = useHistory();
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  //const { register, handleSubmit, control, setValue, reset, watch } = useForm();

  const {
    getSingleBatchPayslip,
    singlebatchpayslip,
    payslip_period,
    singlebatch_payslip_loading,
    singlebatch_payslip_error,
  } = usePayslipsContext();
  const { loadPayitems, payitems } = useTablesContext();
  const payrundata = useRecoilValue(payrunState);
  const [loadFormdata, setLoadFormdata] = useState(false);
  const [loadUpdatedata, setLoadUpdatedata] = useState(false);
  const [formdata, setFormdata] = useState([]);
  const [rowindex, setRowIndex] = useState(0);
  const [showSumm, setShowSumm] = useState(false);
  console.log("payslip period", payslip_period);

  const onFormSubmit = (data) => {
    console.log("Data", data);
  };

  useEffect(() => {
    loadPayitems();
    getSingleBatchPayslip(payslip_period);
  }, []);

  useEffect(() => {
   
    setLoadFormdata(false);
  }, [loadUpdatedata]);

  
  const handleShowSumm = (e) => {
    e.preventDefault();
    setShowSumm(!showSumm);
  };

  const handleEmpButtonClick = (index) => {
       const paydata = singlebatchpayslip[index];
    setFormdata({ ...paydata });
    setFormdata({ ...paydata });
    setLoadFormdata(true);
  };

  if (singlebatch_payslip_loading) {
    return (
      <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
        <div>
          <h2>Loading...Payslips</h2>
        </div>
      </Paper>
    );
  }

  if (singlebatch_payslip_error) {
    return (
      <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
        <div>
          <h2>Internet connection problem!</h2>
        </div>
      </Paper>
    );
  }

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <section className={classes.section}>
        <Grid container direction="row" style={{ border: "1px solid white" }}>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <h2>Employees</h2>
          </Grid>
          <Grid item sm={10} style={{ border: "1px solid white" }}>
            <h2>Payroll Details</h2>
          </Grid>
          <Grid
            item
            sm={2}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <div>
              {singlebatchpayslip &&
                singlebatchpayslip.map((item, index) => {
                  return (
                    <div>
                      <Button
                        className={classes.empbtn}
                        // ${index === value && "activebtn"}
                        onClick={(e) => {
                          setRowIndex(index);
                          handleEmpButtonClick(index);
                        }}
                      >
                        <div key={item.id}> {item.name}</div>
                      </Button>
                      <Divider
                        variant="fullWidth"
                        className={classes.divider}
                      />
                    </div>
                  );
                })}
            </div>
          </Grid>
          <Grid
            item
            sm={10}
            align="center"
            style={{ border: "1px solid white" }}
          >
            <Grid
              container
              direction="row"
              style={{ border: "1px solid white" }}
            >
              <Grid
                item
                sm={12}
                align="left"
                style={{ border: "1px solid white" }}
              >
                <div style={{ padding: 5 }}>
                  <ButtonGroup
                    color="primary"
                    aria-label="outlined primary button group"
                  >
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      className={classes.button}
                      style={{ marginLeft: 5 }}
                    >
                      Save <Icon className={classes.rightIcon}>send</Icon>
                    </Button>

                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      className={classes.button}
                      onClick={(e) => handleShowSumm(e)}
                      style={{ marginLeft: 10 }}
                    >
                      Details <Icon className={classes.rightIcon}>send</Icon>
                    </Button>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      onClick={(e) => handleShowSumm(e)}
                      className={classes.button}
                      style={{ marginLeft: 10 }}
                    >
                      Summary <Icon className={classes.rightIcon}>send</Icon>
                    </Button>
                  </ButtonGroup>
                </div>
              </Grid>
            </Grid>
            {!showSumm && (
              <PayForm
                formdata={formdata}
                setFormdata={setFormdata}
                loadFormdata={loadFormdata}
                setLoadFormdata={setLoadFormdata}
                payitems={payitems}
                setLoadUpdatedata={setLoadUpdatedata}
                rowindex={rowindex}
              />
            )}
            {showSumm && (
              <PaySummary
                payrundata={payrundata}
                singlebatchpayslip={singlebatchpayslip}
              />
            )}
          </Grid>
        </Grid>
      </section>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  button: {
    margin: theme.spacing(1),
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  section: {
    width: "80vw",
    margin: "1rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  empscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  empcontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  empbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  empinfo: {
    fontWeight: "400",
  },
  divider: {
    // Theme Color, or use css color in quote
    background: "white",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 350,
  },
}));

export default Payrunbatch;
