import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Paper from "@material-ui/core/Paper";
import TextField from "@material-ui/core/TextField";
import { Grid } from "@material-ui/core";
import CardLayout from "../helpers/CardLayout";

import { useEmployeesContext } from "../context/employees_context";
import { useTablesContext } from "../context/tables_context";

const drawerWidth = 240;

// const CardLayout = ({ title, children }) => {
//   const classes = useStyles();
//   return (
//     <div>
//       <CardLayout title="Personal Information">
//         <h2>details</h2>
//       </CardLayout>
//     </div>
//   );
// };

const EmployeeView = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [profile, setProfile] = useState({});
  const [family, setFamily] = useState({});

  const {
    employees,
    addEmployee,
    editEmployeeID,
    employees_loading,
    updateEmployee,
    deleteEmployee,
    loadEmployees,
    getSingleEmployee,
    setEditEmployeeID,
    setIsEditingOn,
    setIsEditingOff,
    resetSingleEmployee,
    single_employee,
  } = useEmployeesContext();
  const {
    loadSingleBatchFamily,
    loadFamily,
    singlebatchfamily,
    addFamily,
    deleteFamily,
    updateFamily,
    singlebatch_family_loading,
    singlebatch_family_error,
  } = useTablesContext();

  useEffect(() => {
    load_Profile();
    load_Family();
  }, []);

  const load_Profile = () => {
    //const { empid, name, birthdate, gender, email, phone, ic_no, ic_expirydate } = single_employee;
    setProfile({ ...single_employee });
  };

  const load_Family = () => {
    //loadSingleBatchFamily(editEmployeeID);
    setFamily({ ...singlebatchfamily });
    console.log(family)
    console.log(singlebatchfamily)
  };

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <div className={classes.root}>
        <Grid container spacing={3}>
          <Grid xs={12} md={8} lg={12}>
            <CardLayout title="Profile">
              <div>
                <form className={classes.form}>
                  <TextField
                    label="Name"
                    name="name"
                    value={profile.name}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    label="Phone"
                    name="phone"
                    value={profile.phone}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    label="Email"
                    name="email"
                    value={profile.email}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    label="Birth Date"
                    name="birthdate"
                    value={profile.birhdate}
                    type="date"
                    InputLabelProps={{
                      shrink: true,
                    }}
                  />
                  <TextField
                    label="Gender"
                    name="gender"
                    value={profile.gender}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </form>
              </div>
            </CardLayout>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Personal Information">
                <div>
                  <form>
                    <TextField
                      label="I/C No"
                      name="icno"
                      value={profile.ic_no}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                    <TextField
                      label="I/C Expiry Date"
                      name="ic_expirydate"
                      value={profile.ic_expirydate}
                      type="date"
                      InputProps={{
                        readOnly: true,
                      }}
                      InputLabelProps={{
                        shrink: true,
                      }}
                    />
                    <TextField
                      label="Passport No"
                      name="passportno"
                      value={profile.passportno}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                    <TextField
                      label="Passport Expiry Date"
                      name="passport_expirydate"
                      value={profile.passport_expirydate}
                      type="date"
                      InputLabelProps={{
                        shrink: true,
                      }}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                    <TextField
                      label="Work Permit No"
                      name="workpermitno"
                      value={profile.workpermitno}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                    <TextField
                      label="W/P Expiry Date"
                      name="workpermit_expirydate"
                      value={profile.workpermit_expirydate}
                      type="date"
                      InputLabelProps={{
                        shrink: true,
                      }}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                    <TextField
                      label="Nationality"
                      name="nationality"
                      value={profile.nationality}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                    <TextField
                      label="Marital Status"
                      name="marital_status"
                      value={profile.marital_status}
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Family">
                <div>
                  <form>
                    {/* {family.map((f) => {
                      return (
                        <li>e.name</li>
                      )  
                    
                    })} */}
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Education">
                <div>
                  <form>
                    <TextField label="Name" name="name" value="name" />
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Experiences">
                <div>
                  <form>
                    <TextField label="Name" name="name" value="name" />
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>

          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Training">
                <div>
                  <form>
                    <TextField label="Name" name="name" value="name" />
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>
          <Grid xs={6} md={8} lg={6}>
            <div>
              <CardLayout title="Leaves">
                <div>
                  <form>
                    <TextField label="Name" name="name" value="name" />
                  </form>
                </div>
              </CardLayout>
            </div>
          </Grid>
        </Grid>
      </div>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  form: {
    "& > *": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
}));

export default EmployeeView;
