import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import {
  Button,
  ButtonGroup,
  Grid,
  Icon,
  TextField,
  Divider,
  ListSubheader,
  MenuItem,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";
import { payrunState } from "./data/atomdata";

const columns = [
  {
    title: "Name",
    field: "name",
    editable: "never",
  },
  {
    title: "Wages",
    field: "wages",
    editable: "never",
    type: "currency",
  },
  {
    title: "TAP Amount",
    field: "tapamount",
    editable: "never",
    type: "currency",
  },
  {
    title: "SCP Amount",
    field: "scpamount",
    editable: "never",
    type: "currency",
  },
  {
    title: "Allowances",
    field: "allowancws",
    editable: "never",
    type: "currency",
  },
  {
    title: "Deductions",
    field: "deductions",
    editable: "never",
    type: "currency",
  },
  {
    title: "Nett Pay",
    field: "nett_pay",
    editable: "never",
    type: "currency",
  },
];

const initial_state = [
  {
    name: "",
    period: "",
    date: "",
    payrun: "",
    nett_pay: 0,
    bank_name: "",
    bank_acno: "",
    tap_acno: "",
    tap_amount: 0,
    scp_acno: "",
    scp_amount: 0,
    empid: "",
    status: "",
    allows_type1: " ",
    allows_type1amt: 0,
    allows_type2: " ",
    allows_type2amt: 0,
    allows_type3: " ",
    allows_type3amt: 0,
    allows_type4: " ",
    allows_type4amt: 0,
    allows_type5: " ",
    allows_type5amt: 0,
    allows_type6: " ",
    allows_type6amt: 0,
    allows_type7: " ",
    allows_type7amt: 0,
    allows_type8: " ",
    allows_type8amt: 0,
    deducts_type1: " ",
    deducts_type1amt: 0,
    deducts_type2: " ",
    deducts_type2amt: 0,
    deducts_type3: " ",
    deducts_type3amt: 0,
    deducts_type4: "",
    deducts_type4amt: 0,
    deducts_type5: "",
    deducts_type5amt: 0,
    deducts_type6: "",
    deducts_type6amt: 0,
    deducts_type7: "",
    deducts_type7amt: 0,
    deducts_type8: "",
    deducts_type8amt: 0,
  },
];

const PaySummary = ({ singlebatchpayslip }) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);
  const { payrun, period, paydate } = payrundata;
  const [payrundata, setPayrundata] = useRecoilState(payrunState);

  const handleCalcTotals = () => {
    let totalwages = 0, totaltap = 0, totalscp = 0, totalallows = 0, totaldeducts = 0, totalpayroll = 0
    (singlebatchpayslips && singlebatchpayslip)
     
  }



  return (
    <form>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={12} align="center" style={{ border: "1px solid white" }}>
          <h2>Summary</h2>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={3} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Period"
              name="period"
              variant="filled"
              type="text"
              value={period}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
        <Grid item sm={3} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Pay Run Batch"
              name="payrun"
              variant="filled"
              type="text"
              value={payrun}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
        <Grid item sm={3} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Pay Date"
              name="paydate"
              variant="filled"
              type="date"
              value={paydate}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
        <Grid item sm={3} align="center" style={{ border: "1px solid white" }}>
          <div>
            <TextField
              label="Payroll Total"
              name="paytotal"
              variant="filled"
              type="number"
              value={payrundata.paytotal}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
        </Grid>
      </Grid>

      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={12} align="center" style={{ border: "1px solid white" }}>
