import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import {
  Button,
  Paper,
  Grid,
  Icon,
  Divider,
  TextField,
  NativeSelect,
  InputLabel,
  MenuItem,
} from "@material-ui/core";
import { useRecoilState } from "recoil";
import { siteallowsState } from "./data/atomdata";

//const drawerWidth = 240;
const selectYear = [{ name: "2021", name: "2022" }];
const selectMonth = [
  { name: "January" },
  { name: "February" },
  { name: "March" },
  { name: "April" },
  { name: "May" },
  { name: "June" },
];

const DailyAllowancesAddPeriod = ({ handleDialogClose }) => {
  let date = new Date();
  let longMonth = date.toLocaleString("en-us", { month: "long" });
  //console.log("date", date, longMonth);
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [input, setInput] = useRecoilState(siteallowsState);
  const [alert, setAlert] = useState(false);
  const [error, setError] = useState(false);

  console.log("input", input);
  function formatDate(string) {
    var options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(string).toLocaleDateString("en-GB", options);
  }

  function formatPayrun() {
    // const yy =
    //   input.fromdate.substring(0, 4) + "-" + input.fromdate.substring(5, 7);
    // const mm = input.fromdate.substring(5, 7);
    // const d = input.fromdate;
  }

  function daysInMonth(month, year) {
    return new Date(year, month, 0).getDate();
  }

  const handleChange = (e) => {
    setInput({ ...input, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(input);
  };

  return (
    <Paper className={fixedHeightPaper} style={{ backgroundColor: "black" }}>
      <form onSubmit={(e) => handleSubmit(e)}>
        <Grid
          container
          spacing={0}
          direction="row"
          alignItems="center"
          justify="center"
          style={{ border: "1px solid white" }}
        >
          <h2>Build Site Allowances</h2>
        </Grid>
        <Grid
          container
          spacing={0}
          direction="row"
          alignItems="center"
          justify="center"
          style={{ border: "1px solid white" }}
        >
          <div>
            <Grid
              item
              sm={12}
              style={{ border: "1px solid white" }}
              direction="column"
              align="left"
            >
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Year"
                  id="margin-normal"
                  name="year"
                  defaultValue={input.year}
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                  style={{ width: "100%" }}
                  error={!!error}
                  helperText={error ? error.message : null}
                  select
                >
                  <MenuItem value="Pending">Pending</MenuItem>
                  <MenuItem value="Cancel">Cancel</MenuItem>
                  <MenuItem value="Approve">Approve</MenuItem>
                  <MenuItem value="Reject">Reject</MenuItem>
                </TextField>
                <TextField
                  label="Year"
                  id="margin-normal"
                  name="year"
                  defaultValue={input.year}
                  style={{ width: "100%" }}
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                  error={!!error}
                  helperText={error ? error.message : null}
                  select
                >
                  <MenuItem value="Pending">Pending</MenuItem>
                  <MenuItem value="Cancel">Cancel</MenuItem>
                  <MenuItem value="Approve">Approve</MenuItem>
                  <MenuItem value="Reject">Reject</MenuItem>
                </TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Period"
                  variant="filled"
                  required
                  style={{ width: "100%" }}
                  name="period"
                  value={input.period}
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
                {/* <Divider className={classes.divider} /> */}
                <TextField
                  label="Manager"
                  variant="filled"
                  required
                  style={{ width: "100%" }}
                  name="manager"
                  value={input.manager}
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="District"
                  variant="filled"
                  required
                  value={input.district}
                  style={{ width: "100%" }}
                  name="district"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
                <TextField
                  label="Location/Rig"
                  variant="filled"
                  required
                  value={input.location}
                  style={{ width: "100%" }}
                  name="location"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Operation Type"
                  variant="filled"
                  required
                  value={input.typeoperation}
                  style={{ width: "100%" }}
                  name="typeoperation"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
                <TextField
                  label="Client"
                  variant="filled"
                  required
                  value={input.client}
                  style={{ width: "100%" }}
                  name="client"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Ticket No/Job No"
                  variant="filled"
                  required
                  value={input.jobno}
                  style={{ width: "100%" }}
                  name="jobno"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
                <TextField
                  label="Crew Operation"
                  variant="filled"
                  required
                  value={input.creqoperation}
                  style={{ width: "100%" }}
                  name="crewoperation"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
              <div style={{ display: "flex", flexDirection: "row" }}>
                <TextField
                  label="Job Bonus"
                  variant="filled"
                  required
                  value={input.jobbonus}
                  style={{ width: "100%" }}
                  name="jobbonus"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
                <TextField
                  label="Per Diem"
                  variant="filled"
                  required
                  value={input.perdiem}
                  style={{ width: "100%" }}
                  name="perdiem"
                  className={classes.textField}
                  onChange={(e) => handleChange(e)}
                ></TextField>
              </div>
            </Grid>
          </div>
        </Grid>
        <Grid
          container
          spacing={0}
          direction="row"
          alignItems="center"
          justify="center"
          style={{ border: "1px solid white" }}
        >
          <Button
            type="submit"
            variant="contained"
            color="primary"
            className={classes.button}
          >
            Build <Icon className={classes.rightIcon}>send</Icon>
          </Button>
        </Grid>
      </form>
    </Paper>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  button: {
    margin: theme.spacing(1),
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
